import tokenTypes from '../config/tokenTypes';
import ApiError from '../errors/ApiErrors';
import { verifyDBToken } from '../helpers/token-helper';
import Token from '../models/token-model';
import User from '../models/user-model';

export const isLoggedIn = async (req, res, next) => {
  try {
    const { refreshToken } = req.cookies;

    if (!refreshToken)
      return next(new ApiError(401, 'No refresh token provided'));

    const verifiedRefToken = await verifyDBToken(
      refreshToken,
      tokenTypes.REFRESH
    );

    if (!verifiedRefToken) return next(new ApiError(404, 'Token not found'));

    const user = await User.findById(
      verifiedRefToken.user,
      'acceptTerms role _id'
    ).exec();

    if (!user.acceptTerms || user.acceptTerms !== true)
      return next(new ApiError(404, 'user not found'));

    req.user = user;

    const refreshTokens = await Token.find({ user: req.user.id }).populate(
      'user'
    );

    req.user.ownsToken = (token) =>
      !!refreshTokens.find((x) => x.token === token);

    next();
  } catch (error) {
    next(error);
  }
};

const isInvalidInput = (input) => {
  const containsHtml = /<[^>]*>/;
  const startsWithEqual = /^\s*=/;
  return containsHtml.test(input) || startsWithEqual.test(input);
};

export const inputValidator = (req, res, next) => {
  let invalid_input_count = 0;
  const obj = req.body;
  const stack = [obj];
  while (stack?.length > 0) {
    const currentObj = stack.pop();
    Object.keys(currentObj).forEach((key) => {
      if (typeof currentObj[key] === 'object' && currentObj[key] !== null) {
        stack.push(currentObj[key]);
      } else {
        if (
          typeof currentObj[key] === 'string' &&
          isInvalidInput(currentObj[key])
        ) {
          invalid_input_count++;
        }
      }
    });
  }

  if (invalid_input_count === 0) {
    return next();
  } else {
    return res
      .status(400)
      .json({ success: false, error_code: 'invalid_input' });
  }
};

// app.use(inputValidator);

